DROP DATABASE IF EXISTS project_MA_Twente;
CREATE DATABASE project_MA_Twente;
USE project_MA_Twente;

DROP TABLE IF EXISTS configuraties;
CREATE TABLE configuraties(
    cid INT AUTO_INCREMENT,
    besturingssysteem VARCHAR(20),
    type VARCHAR(20),
    model VARCHAR(20),
    proccessor VARCHAR(20),
    werkgeheugen VARCHAR(20),
    videokaart VARCHAR(20),
    harddrive VARCHAR(20),
    psu VARCHAR(20),
    bouwjaar DATE,
    licenties VARCHAR(20),
    PRIMARY KEY (cid)
);


DROP TABLE IF EXISTS gebruikers;
CREATE TABLE gebruikers(
    gid INT AUTO_INCREMENT,
    voorletter CHAR(1),
    achternaam VARCHAR(20),
    geslacht VARCHAR(5),
    intern_telefoon_nummer INT,
    afdeling VARCHAR(40),
    postcode CHAR(6),
    email VARCHAR(255),
    PRIMARY KEY (gid)
);

USE project_MA_Twente;

DROP TABLE IF EXISTS incidenten;
CREATE TABLE incidenten(
    iid INT AUTO_INCREMENT,
    gebruikers_gid INT,
    configuraties_cid INT,
    KorteIncidentBeschrijving VARCHAR(90),
    volledigeIncidentBeschrijving VARCHAR(600),
    betrekkingOpAantalGebruikers INT,
    afhandelTijd DATE,
    verantwoordelijke VARCHAR(40),
    oorzaken VARCHAR(30),
    oplossingen VARCHAR(400),
    terugkoppeling VARCHAR(400),
    status VARCHAR(12),
    PRIMARY KEY (iid)
);

